package com.lc.leetcode;

import com.google.common.base.Stopwatch;
import org.codehaus.groovy.runtime.powerassert.SourceText;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.TimeUnit;

/**
 * Created by caoling on 2016/11/3.
 * Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.
 * <p>
 * Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.
 * <p>
 * The order of output does not matter.
 * <p>
 * Example 1:
 * <p>
 * Input:
 * s: "cbaebabacd" p: "abc"
 * <p>
 * Output:
 * [0, 6]
 * <p>
 * Explanation:
 * The substring with start index = 0 is "cba", which is an anagram of "abc".
 * The substring with start index = 6 is "bac", which is an anagram of "abc".
 * Example 2:
 * <p>
 * Input:
 * s: "abab" p: "ab"
 * <p>
 * Output:
 * [0, 1, 2]
 * <p>
 * Explanation:
 * The substring with start index = 0 is "ab", which is an anagram of "ab".
 * The substring with start index = 1 is "ba", which is an anagram of "ab".
 * The substring with start index = 2 is "ab", which is an anagram of "ab".
 */
public class T438 {

    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();

        if (s != null && s.length() > 0
                && s.length() >= p.length()) {

            int pLength = p.length();
            int maxResultLength = s.length() - p.length() + 1;

            int[] pData = new int[26];
            int startPos = (int) 'a';


            for (char c : p.toCharArray()) {
                int pos = (int) c - startPos;
                pData[pos]++;
            }

            int lastSuccessPos = -1;
            boolean success;

            int[] newData = new int[26];

            for (int i = 0; i < maxResultLength; i++) {
                success = true;
                if (lastSuccessPos > 0 && lastSuccessPos == (i - 1)) {
                    if (s.charAt(lastSuccessPos) == s.charAt(i + pLength - 1)) {
                        result.add(i);
                        lastSuccessPos = i;
                    } else {
                        continue;
                    }
                } else {

                    for (int k = 0; k < 26; k++) {
                        newData[k] = 0;
                    }

                    for (int j = 0; j < pLength; j++) {
                        int newPose = (int) s.charAt(i + j) - startPos;
                        newData[newPose]++;
                        if (newData[newPose] > pData[newPose]) {
                            success = false;
                            break;
                        }
                    }
                    if (success) {
                        result.add(i);
                        lastSuccessPos = i;
                    }
                }


            }


        }

        return result;
    }


    public static void main(String[] args) {
        String s = "";
        String p = "";

        Stopwatch stopwatch = Stopwatch.createUnstarted();

        for (int i = 0; i < 10; i++) {
            stopwatch.reset();
            stopwatch.start();
            System.out.println(findAnagrams(s, p));
            stopwatch.stop();
            System.out.println(stopwatch.elapsed(TimeUnit.MILLISECONDS));
        }

    }
}
